import './App.css';
import SearchBar from './SearchBar';
import Video from './Video';
import VideoList from './VideoList';

import React, { Component } from 'react'
import axios from 'axios';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      videolist: [],
    };
  }

  getVideoList(items) {
    console.log({items});
  }

  getSearchInfo = e => {
    let url = 'https://www.googleapis.com/youtube/v3/search?part=snippet&type=video&key=AIzaSyCbIFGj95IKPzAxOINq8kjDpeTg4bW5EhE&q='+e;
    axios.get(url)
      .then(function (response){
        let items = response.data.items;
        this.getVideoList(items);
      })
      .catch (function (error){
        console.log(error);
      })
  }

  render() {
    return (
      <div>
        <div className='row-header'>
          <div className='col-full'>
            <SearchBar
              onClick = {this.getSearchInfo}
             />
          </div>
        </div>
        <div className='row-body'>
          <div className='collumn left'>
            <Video />
          </div>
          <div className='collumn right'>
            <VideoList />
          </div>
        </div>
      </div>
    )
  }
}


export default App;
