import './searchbar.css'
import React, {Component} from 'react'

class SearchBar extends Component {

    constructor(pros) {
        super(props);
        this.onButtonCliked = this.onButtonCliked.bind;
    }

    onButtonCliked() {
        console.log(this.refs);
    }

    render() {
        return (
            <div className='bar'>
                <input type='text' className='textbar' ref='stuffName'>

                </input>
                <button 
                    className='btt-submit'
                    onClick = {this.onButtonCliked}>
                    Go
                </button>
            </div>
        )
    }
}

export default SearchBar;
